mapper.insert:
INSERT INTO game_guides (
    title, content, category_id, cover_image, video_url,
    view_count, like_count, status, created_at, updated_at
) VALUES (
             #{title}, #{content}, #{categoryId}, #{coverImage}, #{videoUrl},   
             #{viewCount}, #{likeCount}, #{status}, #{createdAt}, #{updatedAt}
         )

mapper.update:
UPDATE game_guides
title = #{title}, content = #{content}, category_id = #{categoryId}, cover_image = #{coverImage}, video_url = #{videoUrl}, view_count = #{viewCount}, like_count = #{likeCount}, status = #{status}, updated_at = NOW()
WHERE id = #{id}
